import fetch from 'node-fetch'
import {
  initialize,
  WORKFLOW_EXAMPLE,
  WORKFLOW_EXECUTION_EXAMPLE,
} from '@phonal-technologies/drafterai-js'
// Init an API with apikey and fetch provider
