const drafterApi = initialize(accessKey, {
  fetch,
})
;(async () => {
  console.log('Workflow execution example', WORKFLOW_EXECUTION_EXAMPLE)
  const wfExecutions = await drafterApi.workflowExecutions.create({
    "workflowId": 110,
    "context": { "redditTopic": "Suggest eCommerce Product Info"  }
  })

  console.log('Workflow executions', wfExecutions)
})

